import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [{name: 'Jasu', age: 24},
    {name: 'Jasu2', age: 27},
    {name: 'Jasusa', age: 35}
  ]

  }


  render() {
    return (
      <div className="App">
        <h1> Hola, somos springmaniacos</h1>
        <p>¿Están ahí mis vidas? Están ahí? me oyeeen, me escuchan</p>
        <button>Switch Name</button>
        <Person name={this.state.persons[0].name} age={this.state.persons[0].age}>Datos adicionales</Person>
        <Person name={this.state.persons[1].name} age={this.state.persons[1].age}>Datos adicionales</Person>
        <Person name={this.state.persons[2].name} age={this.state.persons[2].age}>Datos adicionales</Person>
      </div>
    );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'holiwis'));
  }
}

export default App;
