import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [{name: 'Jasu', age: 24},
    {name: 'Jasu2', age: 27},
    {name: 'Jasusa', age: 35}
  ],
    otherState: 'valor random',
    showPersons: 'false'
  }

  nameChangeHandler = (event) =>{
    this.setState( {
      persons: [{name: 'Jasu', age: 24},
      {name: event.target.value, age: 27},
      {name: 'Jasusa', age: 35}
    ]
})
  }
  togglePersonsHandler =() =>{
     const doesShow = this.state.showPersons;
     this.setState({showPersons: !doesShow})
  }

  switchNameHandler = (newName) => {
    // console.log('Was clicked!');
    //NO VA:  this.state.persons[0].name='Jesús Galera';
    this.setState( {
      persons: [{name: newName, age: 24},
    {name: 'Jasu2', age: 27},
    {name: 'Jasusa', age: 35}
  ]
})
  }

  deletePersonHandler = (personIndex) =>{
    const persons = this.state.persons;
    persons.splice(personIndex, 1);
    this.setState({showPersons: !doesShow});
  }


  render() {
    const style = {
      backgroundColor: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    };

    let persons = null;

    if(this.state.showPersons){
      persons = (
        <div>
        {this.state.persons.map((person, index) =>{
          return <Person
          name={person.name}
          age={person.age}
          click={() => this.deletePersonHandler(index)}
          />
        })}
      </div>
      );
    }

    return (

      <div className="App">
        <h1> Hola, somos springmaniacos</h1>
        <p>¿Están ahí mis vidas? Están ahí? me oyeeen, me escuchan</p>
        <button
         style={style}
         onClick={this.togglePersonsHandler}>Hidde people</button>
        {persons}
      </div>
    );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'holiwis'));
  }
}

export default App;
